    /**
     * 解法一：
     * 暴力遍历
     * @param s
     * @param t
     * @return
     */
    private boolean isSubsequence(String s, String t) {
        if(s==null || s=="") {
            return true;
        }

        if(t==null || t=="") {
            return false;
        }
        return isSubsequence(s, 0, t, 0);
    }

    private boolean isSubsequence(String s, int indexS, String t, int indexT) {
        if(indexS > s.length()-1) {
            return true;
        }

        if(indexT > t.length()-1) {
            return false;
        }

        int newIndexT = indexT;
        for(int n = indexT; n < t.length(); n ++) {
            if(s.charAt(indexS) == t.charAt(n)) {
                break;
            } else {
                newIndexT ++;
            }
        }

        if(newIndexT > t.length()-1) {
            return false;
        }

        return isSubsequence(s, indexS+1, t, newIndexT+1);
    }

    /**
     * 解法二：
     * 优化解法一代码，采用双指针优化。
     * @param s
     * @param t
     * @return
     */
    private boolean isSubsequence1(String s, String t) {
        int lenS = s.length();
        int lenT = t.length();

        int left = 0, right = 0;
        while(left < lenS && right < lenT) {
            if(s.charAt(left) == t.charAt(right)) {
                left ++;
            }
            right ++;
        }

        return left==lenS;
    }

